class DrawingGroup(QGroupBox):
    shell = client.Dispatch("WScript.Shell")
    adobe_reader = Application(backend="uia")
    mspaint = Application(backend="win32")
    pic_name_find1 = "{}.png".format(os.getpid())
    pic_name_find2 = "{}.png".format(os.getpid())

    def __init__(self):
        super().__init__()

        self.drawing_layout = QGridLayout()
        self.drawing_loc_le = QLineEdit()
        self.drawing_loc_le.setPlaceholderText(drawing_path)
        self.drawing_combo = QComboBox()
        self.drawing_combo.addItems(dpi_list)
        self.drawing_combo.setCurrentIndex(1)
        self.drawing_button_select = QPushButton("도면선택 + 이미지변환")
        self.drawing_button_select.clicked.connect(self.selectButtonClicked)
        self.drawing_tabs = QTabWidget()
        self.drawing_label = [QLabel('PDF-image',self) for i in range(10)]
        for idx, label in enumerate(self.drawing_label):
            label.setFixedSize(600,400)
            label.setAlignment(Qt.AlignCenter)
            font = label.font()
            font.setPointSize(40)
            label.setFont(font)
            self.drawing_tabs.addTab(label, str(idx+1) + ' ')
        self.drawing_layout.addWidget(self.drawing_loc_le,0,0,1,4)
        self.drawing_layout.addWidget(self.drawing_combo,0,4,1,1)
        self.drawing_layout.addWidget(self.drawing_button_select,0,5,1,2)
        self.drawing_layout.addWidget(self.drawing_tabs,1,0,7,7)
        self.setLayout(self.drawing_layout)

    # 도면(pdf) 파일을 찾아서 이미지(png) 파일로 변경합니다. 
    def selectButtonClicked(self):
        self.dwg_filename = QFileDialog.getOpenFileName(self, 'Open file', window.drawing_path)
        updated_path = os.path.dirname(self.dwg_filename[0])
        window.template_path = updated_path

        if not self.dwg_filename[0]:
            QMessageBox.about(self, "Warning", "Drawing 파일을 선택되지 않았습니다.")
            return
        
        self.drawing_loc_le.setText(self.dwg_filename[0])
        window.terminal_group.terminal_browser.append(self.dwg_filename[0])
    
        try:
            pages = convert_from_path(self.dwg_filename[0], dpi=int(self.drawing_combo.currentText()))
        except:         # DRM에 걸려 있는경우 직접 PDF파일을 캡쳐해서 저장함
            print("Unexpected error:", sys.exc_info()[0])
            reply = QMessageBox.question(self, "Warning", "PDF 파일이 DRM에 걸려 있습니다. 직접 선택 영역을 직접 선택하시겠습니까?",
                                QMessageBox.Yes | QMessageBox.No, QMessageBox.No)

            self.drawing_label[0].setText('DRM')
            window.finding_group.finding_obj_label_1.setText('DRM')
            window.finding_group.finding_obj_label_2.setText('DRM')

            if reply == QMessageBox.Yes:
                self.adobe_reader.start(cmd_line = 'C:\Program Files (x86)\Adobe\Acrobat Reader DC\Reader\AcroRd32.exe'+ ' ' + self.dwg_filename[0])
                self.adobe_reader.connect(path='C:\Program Files (x86)\Adobe\Acrobat Reader DC\Reader\AcroRd32.exe')
                time.sleep(0.1)

                try:
                    self.adobe_reader_window_name = os.path.basename(self.dwg_filename[0]) + " - Adobe Acrobat Reader DC"
                    self.adobe_reader_window_id = win32gui.FindWindow(None, self.adobe_reader_window_name)
                    window.terminal_group.terminal_browser.append(os.path.basename(self.dwg_filename[0]) + " - Adobe Acrobat Reader DC" + " opened with PID: " + str(self.adobe_reader_window_id))
                    window.terminal_group.terminal_browser.append(os.path.basename("opened with PID: " + str(self.adobe_reader_window_id) + "name" + self.adobe_reader_window_name))
                    self.findAndCaptureDrmDrawing(page_no=1)
                    self.findAndCaptureDrmDrawing(page_no=2)
                   
                except:
                    self.adobe_reader_window_name = os.path.basename(self.dwg_filename[0]) + " - Adobe Acrobat Reader DC (32-bit)"
                    self.adobe_reader_window_id = win32gui.FindWindow(None, self.adobe_reader_window_name)
                    window.terminal_group.terminal_browser.append(os.path.basename(self.dwg_filename[0]) + " - Adobe Acrobat Reader DC (32-bit)" + " opened with PID: " + str(self.adobe_reader_window_id))
                    window.terminal_group.terminal_browser.append(os.path.basename("opened with PID: " + str(self.adobe_reader_window_id) + "name" + self.adobe_reader_window_name))
                    self.findAndCaptureDrmDrawing(page_no=1)
                    self.findAndCaptureDrmDrawing(page_no=2)

                self.adobe_reader.kill()
                time.sleep(0.5)

                return
            else:
                return
        png_files = glob.glob(os.path.abspath(drawing_img_path + r'\*.png'))
        print(png_files)
        for png_file in png_files:
            try:
                os.remove(png_file)
            except OSError as e:
                print(f"Error:{e.strerror}")
                QMessageBox.about(self, "Warning", f"Error:{e.strerror}")

        
        for i, page in enumerate(pages):
            dwg_img_filename = drawing_img_path+os.path.basename(self.dwg_filename[0])[:-4]
            page.save(f'{dwg_img_filename}_page{i+1:0>2d}.png','PNG')
            window.terminal_group.terminal_browser.append(f'{dwg_img_filename}_page{i+1:0>2d}.png saved...')
        window.terminal_group.terminal_browser.append('Done !')

        self.dwg_img_files = [ f for f in listdir(drawing_img_path) if isfile(join(drawing_img_path,f)) ]
        self.images = [cv2.imread(file) for file in glob.glob(drawing_img_path + "/*.png")]

        for i in range(0,len(self.dwg_img_files)):
            pic = QPixmap(drawing_img_path +self.dwg_img_files[i])
            pic = pic.scaledToWidth(600)
            self.drawing_label[i].setPixmap(QPixmap(pic))
        return

    def findAndCaptureDrmDrawing(self,page_no = 1): 
        text_message = ["Contents","Revision"]
        
        win32gui.SetForegroundWindow(self.adobe_reader_window_id)
        time.sleep(0.2)
        self.shell.SendKeys('^0',0)
        msg = ctypes.windll.user32.MessageBoxW(None, f"도면에서 {text_message[page_no-1]}(확대) 영역을 선택하세요.", "확대", 0x00040040)
        self.shell.SendKeys('{F10}')
        time.sleep(0.2)
        self.shell.SendKeys('V')
        time.sleep(0.2)
        self.shell.SendKeys('Z')
        time.sleep(0.2)
        self.shell.SendKeys('M')

        time.sleep(0.5)
        # Collect mouse events until released
        qd_event.mouse_listener()
        time.sleep(0.5)
        msg = ctypes.windll.user32.MessageBoxW(None, f"도면에서 {text_message[page_no-1]}(캡쳐) 영역을 선택하세요.", "캡쳐", 0x00040040)
        win32gui.SetForegroundWindow(self.adobe_reader_window_id)
        self.shell.SendKeys('{F10}')
        time.sleep(0.2)
        self.shell.SendKeys('E')
        time.sleep(0.2)
        self.shell.SendKeys('A')

        time.sleep(0.5)
        # Collect mouse events until released
        qd_event.mouse_listener()
        time.sleep(0.5)

        self.mspaint.start(cmd_line = 'C:\Windows\system32\mspaint.exe', work_dir=find_result_path)
        self.mspaint.connect(path = 'C:\Windows\system32\mspaint.exe')

        mspaint_window_id = win32gui.GetForegroundWindow()
        print('mspaint_window_id : ', mspaint_window_id)
        win32gui.SetForegroundWindow(mspaint_window_id)
        time.sleep(0.2)
        self.shell.SendKeys("^e",0)
        time.sleep(0.2)
        self.shell.SendKeys("%w",0)
        time.sleep(0.2)
        self.shell.SendKeys("100")
        time.sleep(0.2)
        self.shell.SendKeys("%h",0)
        time.sleep(0.2)
        self.shell.SendKeys("100")
        time.sleep(0.2)
        self.shell.SendKeys("{ENTER}")
        time.sleep(0.2)
        self.shell.SendKeys("^v",0)
        time.sleep(0.2)
        self.shell.SendKeys("{F12}",0)
        time.sleep(1)
        if page_no == 1: 
            try:
                os.remove(os.path.abspath(find_result_man_path) + '\\find_1.png')
            except OSError as e:
                print(f"Error:{e.strerror}")
            
            self.pic_name_find1 = os.path.abspath(find_result_man_path) + '\\find_1.png'
            self.shell.SendKeys(self.pic_name_find1)

        elif page_no == 2: 
            try:
                os.remove(os.path.abspath(find_result_man_path) + '\\find_2.png')
            except OSError as e:
                print(f"Error:{e.strerror}")

            self.pic_name_find2 = os.path.abspath(find_result_man_path) + '\\find_2.png'
            self.shell.SendKeys(self.pic_name_find2)

        time.sleep(2)
        self.shell.SendKeys("%s",0)
        time.sleep(2)
        self.mspaint.kill()
        print(self.pic_name_find1)
        if page_no == 1: 
            pic = QPixmap(self.pic_name_find1)
            pic = pic.scaledToWidth(600)
            window.finding_group.finding_match_label_1.setPixmap(QPixmap(pic))
        elif page_no == 2:
            pic = QPixmap(self.pic_name_find2)
            pic = pic.scaledToWidth(600)
            window.finding_group.finding_match_label_2.setPixmap(QPixmap(pic))
        
        time.sleep(0.5)
        return

