    # 찾아낸 그림의 문자를 분석합니다...이미지 투 문자 (OCR)
    # 요게 잘 안되요... 업데이트가 필요합니다.
    def findAnalyzeButtonClicked(self):
        #이미지 매칭 검색
        filename_1 = "{}.png".format(os.getpid())
        final_match_val_1 = 0
        match_val = 0
        top_left = 0
        try:
            final_image_1 = window.drawing_group.images[0]
        except:
            QMessageBox.about(self, "Warning", "도면을 선택되지 않았습니다.")
            return
        
        for n in range(0,len(window.drawing_group.dwg_img_files)):
            for i, method_name in enumerate(methods):
                method = eval(method_name)
                try:
                    res = cv2.matchTemplate(window.drawing_group.images[n], self.obj_1, method)
                except:
                    QMessageBox.about(self, "Warning", "찾고자하는 파일1을 선택하지 않았습니다.")
                    return

                min_val, max_val, min_loc, max_loc = cv2.minMaxLoc(res)

                if method in [cv2.TM_SQDIFF, cv2.TM_SQDIFF_NORMED]:
                    top_left = min_loc
                    match_val = min_val
                else:
                    top_left = max_loc
                    match_val = max_val

            if final_match_val_1 < match_val:
                final_match_val_1 = match_val
                final_image_1 = window.drawing_group.images[n][top_left[1]:top_left[1]+self.obj_1.shape[0], top_left[0]:top_left[0]+self.obj_1.shape[1]]
        
        filename_2 = "{}.png".format(os.getpid())
        final_match_val_2 = 0
        match_val = 0
        top_left = 0
        try:
            final_image_2 = window.drawing_group.images[0]
        except:
            QMessageBox.about(self, "Warning", "도면을 선택되지 않았습니다.")
            return

        for n in range(0,len(window.drawing_group.dwg_img_files)):
            for i, method_name in enumerate(methods):
                method = eval(method_name)
                try:
                    res = cv2.matchTemplate(window.drawing_group.images[n], self.obj_2, method)
                except:
                    QMessageBox.about(self, "Warning", "찾고자하는 파일1을 선택하지 않았습니다.")
                    return
                min_val, max_val, min_loc, max_loc = cv2.minMaxLoc(res)

                if method in [cv2.TM_SQDIFF, cv2.TM_SQDIFF_NORMED]:
                    top_left = min_loc
                    match_val = min_val
                else:
                    top_left = max_loc
                    match_val = max_val

            if final_match_val_2 < match_val:
                final_match_val_2 = match_val
                final_image_2 = window.drawing_group.images[n][top_left[1]:top_left[1]+self.obj_2.shape[0]+500, top_left[0]:top_left[0]+self.obj_2.shape[1]]
        
        text = pytesseract.image_to_string(final_image_1, config=custom_config)
        text += pytesseract.image_to_string(final_image_2, config=custom_config) 
        
        reader = easyocr.Reader(['ko','en'])
        result = reader.readtext(image=final_image_1)
        result += reader.readtext(image=final_image_2)
        
        for word in result: 
            window.terminal_group.terminal_browser.append("word:" + word[1] + "   (match rate:" + str(word[2]) + "%)")

        lines = text.split("\n")
        lines = list(filter(None,lines))
        while ' ' in lines:
            lines.remove(' ')

        version_info = str()
        search = "2020"
        date_pos = int()

        for line in reversed(lines):
            if search in line:
                version_info = line
                break

        for i, alpabet in enumerate(version_info):
            if version_info[i] == '2' and version_info[i+1] == '0' and version_info[i+2] == '2':
                date_pos = i
                break

        window.info_group.info_le[2].setText(version_info[version_info.find(' ')-1:version_info.find(' ')])
        window.info_group.info_le[3].setText(version_info[date_pos:date_pos+10])
        window.info_group.info_le[4].setText(version_info[date_pos-12:date_pos-3])
        window.info_group.info_le[5].setText(version_info[version_info.find(' ')+1:date_pos-14])
        
        text = text.replace("\n\n","\n")
        text = text.replace(" \n","")
        window.terminal_group.terminal_browser.append(text)

        cv2.imwrite(filename_1, final_image_1)
        pic = QPixmap(filename_1)
        self.pic_name_find1 = f'{find_result_path}find_1.png'
        pic.save(self.pic_name_find1,'PNG')
        pic_display=pic.scaledToWidth(600)
        self.finding_match_label_1.setPixmap(pic_display)
        self.finding_tabs.setTabText(1, os.path.basename(self.pic_name_find1))
        os.remove(filename_1)

