        try:
            text = pytesseract.image_to_string(Image.open(self.pic_name_find1)) 
            text += pytesseract.image_to_string(Image.open(self.pic_name_find2))
        except OSError as e:
            print(e)
            QMessageBox.about(self, "Warning", f"Error:{e.strerror}")
            return

        reader = easyocr.Reader(['ko','en'])
        result = reader.readtext(image=self.pic_name_find1,detail=0)
        result += reader.readtext(image=self.pic_name_find2,detail=0)
        
        for word in result: 
            window.terminal_group.terminal_browser.append(word)

        lines = text.split("\n")
        lines = list(filter(None,lines))
        while ' ' in lines:
            lines.remove(' ')

        version_info = str()
        search = "2020"
        date_pos = int()

        for line in reversed(lines):
            if search in line:
                version_info = line
                break

        for i, alpabet in enumerate(version_info):
            if version_info[i] == '2' and version_info[i+1] == '0' and version_info[i+2] == '2':
                date_pos = i
                break

        window.info_group.info_le[2].setText(version_info[version_info.find(' ')-1:version_info.find(' ')])
        window.info_group.info_le[3].setText(version_info[date_pos:date_pos+10])
        window.info_group.info_le[4].setText(version_info[date_pos-12:date_pos-3])
        window.info_group.info_le[5].setText(version_info[version_info.find(' ')+1:date_pos-14])

        text = text.replace("\n\n","\n")
        text = text.replace(" \n","")
        window.terminal_group.terminal_browser.append(text)
        
        window.info_group.info_le[0].setText(text[text.find("Project",):text.find("\n",text.find("Project",),)].replace("Project Name ","")) 
        window.info_group.info_le[6].setText(text[text.find("H/W",):text.find("\n",text.find("H/W",),)].replace("H/W, S/W Ver. ","")) 
        window.info_group.info_le[7].setText(text[text.find("OEM",):text.find("\n",text.find("OEM",),)].replace("OEM P/NO ",""))
        window.info_group.info_le[8].setText(text[text.find("MANDO",):text.find("\n",text.find("MANDO",),)].replace("MANDO P/No",""))
        window.info_group.info_le[9].setText(text[text.find("Supplier",):text.find("\n",text.find("Supplier",),)].replace("Supplier P/No ",""))
        window.info_group.info_le[10].setText(text[text.find("NSR No. :",):text.find("\n",text.find("NSR No.",),)].replace("NSR No. : ",""))
        window.info_group.info_le[12].setText(datetime.today().strftime('%Y%m%d'))

        file = open('info.txt','w')
        file.writelines(str(text.encode('utf-8-sig')))
        file.close()
        return
   
