                        load_history.cell(row = row_, column=col_).value = window.info_group.info_le[2].text() # Rev. ver.
                        load_history.cell(row = row_, column=col_+1).value = window.info_group.info_le[3].text() + '(' + window.info_group.info_le[4].text()+')' #EO No. & Rev.date 
                        load_history.cell(row = row_, column=col_+2).value = window.info_group.info_le[5].text() # Rev. History

                        excape_loop = True

                        if load_history.cell(row =row_-1, column=end_col+1).value is None:      ##@@ end_col+1 : signiture
                            
                            load_history.cell(row = row_-1, column=end_col+1).value = 'SIGNED'
